import axios from "axios"
const res = () => {axios.get('http://localhost:3000/posts').then(res => res.data)}

 export const getAll = () => {
    return dispatch => {
      const res =axios.get('http://localhost:3000/posts')
        .then(user =>dispatch (
            {type: 'GETIT',payload: user}) 
        )

        .catch(err=>dispatch( {
            type: 'GET_ALL_SERVICES_FAILED',
            payload: err
          }))
    }
 }
