import axios from "axios"
const res = () => {axios.get('http://localhost:3000/posts')}

 export const getAll = () => {
    return dispatch => {
        res()
        .then (user =>dispatch (
            {type: 'GETIR',payload: user}) 
        )

        .catch(err=>dispatch( {
            type: 'GET_ALL_SERVICES_FAILED',
            payload: err
          }))
    }
 }
