import logo from './logo.svg';
import './App.css';
import {useSelector,useDispatch} from 'react-redux'
import {getAll,addOne} from './action'

import {useEffect,useState} from 'react';

function App() {
  const dispatch =useDispatch()
  const user = useSelector((state)=>state.Redit.user);
  useEffect(()=>{
    dispatch( getAll())
  },[])
  const [movie,setMovie]=useState({title:'',author:''})
  const test = (movie)=>{
    dispatch(addOne(movie))
  }

  return (
    <div className="App">
      {user.map(el=>(
        <div>
          {el.title}
        </div>
      ))}
      <form onSubmit={test}>
        <input type="text" placeholder='title' onChange={(e)=>setMovie({...movie,})} />
        <input type="text" placeholder='author' />
        <input type="submit"   />
      </form>
    </div>
  );
}

export default App;
