  const addIt = (e) =>{
     e.preventDefault()
     props.test({[e.target.name]:[e.target.value]})

 }
 const saveIt = () =>{
    dispatch(props.action(props.user))
 }


    return (
        <div>
            <form  onSubmit={saveIt}>

               {props.name.map(el=>(

<div  >
<input type="text" name={el} onChange={(e)=>addIt(e)}  />

</div>

               ))}
               <input type='submit' />

</form>

        </div>
    )
}

export default Form
