import axios from "axios"

 export const getAll = () => {
    return dispatch => {
        axios.get('http://localhost:3000/posts')
        .then(user =>dispatch (
            {type: 'GETIR',payload: user}) 
        )

        .catch(err=>dispatch( {
            type: 'GET_ALL_SERVICES_FAILED',
            payload: err
          }))
    }
 }
